      remainders = #{remainders,jdbcType=INTEGER},
      commission = #{commission,jdbcType=DECIMAL},
      qrcodepath = #{qrcodepath,jdbcType=VARCHAR},
      state = #{state,jdbcType=INTEGER}
    where id = #{id,jdbcType=INTEGER}
